import React from 'react';

const {useState} = React;

function App() {
  const [counter, setCounter] = useState(0)

  return (
    <div className="counter"> 
      <h1>React Counter</h1>
      <p className="display-count">{counter}</p>
      <button onClick = { () => {
        setCounter(counter + 1)
      }}>Increase</button>
      <button onClick = { () => {
        setCounter(counter - 1)
      }}>Decrease</button>
    </div>
  );
}

export default App;
